import React, { type ReactNode } from 'react';
import type { GroupBase } from 'react-select';
import {
  AsyncCreatableSelectInput,
  type AsyncCreatableSelectInputProps,
  AsyncSelectInput,
  type AsyncSelectInputProps,
  CreatableSelectInput,
  type CreatableSelectInputProps,
  FieldWrapper,
  type FormLabelProps,
  SelectInput,
  type SelectInputProps,
} from '..';

type SelectFieldProps<
  Option,
  IsMulti extends boolean,
  Group extends GroupBase<Option>,
> = {
  id: string;
  name: string;
  label: ReactNode;
  hint?: ReactNode;
  placeholder?: string;
  error?: ReactNode;
  required?: boolean;
  formLabelProps?: FormLabelProps;
  value: SelectInputProps<Option, IsMulti, Group>['value'];
  onChange: SelectInputProps<Option, IsMulti, Group>['onChange'];
  selectInputProps?: SelectInputProps<Option, IsMulti, Group>;
};

export function SelectField<
  Option,
  IsMulti extends boolean,
  Group extends GroupBase<Option>,
>({
  id,
  name,
  label,
  hint,
  error,
  required,
  placeholder,
  formLabelProps,
  value,
  onChange,
  selectInputProps,
}: SelectFieldProps<Option, IsMulti, Group>): JSX.Element {
  return (
    <FieldWrapper
      formLabelProps={formLabelProps}
      id={id}
      required={required}
      error={error}
      hint={hint}
      label={label}
    >
      <SelectInput<Option, IsMulti, Group>
        {...selectInputProps}
        id={id}
        name={name}
        value={value}
        placeholder={placeholder}
        onChange={onChange}
        error={!!error}
      />
    </FieldWrapper>
  );
}

