import React from "react";
import { createRoot } from "react-dom/client";

import {
  connect,
  IntentCtx,
  RenderItemFormSidebarPanelCtx,
  ItemType,
} from "datocms-plugin-sdk";
import "datocms-react-ui/styles.css";
import ConfigScreen from "./entrypoints/ConfigScreen";
import { render } from "./utils/render";

const container = document.getElementById("root");

connect({
  renderConfigScreen(ctx) {
    return render(<ConfigScreen ctx={ctx} />);
  },
  itemFormSidebarPanels(model: ItemType, ctx: IntentCtx) {
    return [
      {
        id: "goToWebsite",
        label: "Go to website",
        startOpen: true,
      },
    ];
  },
  renderItemFormSidebarPanel(
    sidebarPanelId,
    ctx: RenderItemFormSidebarPanelCtx,
  ) {
    if (container) {
      const root = createRoot(container);
      root.render(
        <React.StrictMode>
          <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexDirection: 'column',
            width: '100%',
            minHeight: '200px',
          }}>
            <a
              href={`http://localhost:4322/preview/?id=${ctx?.item?.id}&locale=${ctx.locale}&type=${ctx.itemType?.attributes?.api_key}`}
              target="_blank"
              rel="noreferrer"
              style={
                {
                  display: 'block',
                  paddingBottom: '12px',
                  paddingTop: '12px',
                  paddingLeft: '16px',
                  paddingRight: '16px',
                  backgroundColor: '#0F6FDE',
                  color: 'white',
                  minHeight: '48px',
                  fontWeight: 500,
                  fontSize: '16px',
                  fontFamily: 'sans-serif',
                  borderRadius: '12px',

                }
              }
              onMouseEnter={(e) => {
                (e.target as HTMLElement).style.boxShadow = '0px 4px 12px rgba(0, 0, 0, 0.2)'; // Ombre au survol
              }}
              onMouseLeave={(e) => {
                (e.target as HTMLElement).style.boxShadow = 'none'; // Supprimer l'ombre quand la souris quitte
              }}
            >
              Preview
            </a>
          </div>
        </React.StrictMode>,
      );
    }
  },
});
